    <script>
        function loadEditor(selector)
        {
            tinymce.init({
                plugins: "code textcolor colorpicker fullscreen link paste image link emoticons spoiler mention autoresize",
                skin: "lightgray",
                content_css: '/Content/Themes/Bootstrap/Site.css',
                toolbar: "undo redo | bold italic underline strikethrough | removeformat forecolor | emoticons | hr spoiler blockquote | link image | bullist numlist | fullscreen code",
                fontsize_formats: '0.8em 0.9em 1.0em 1.1em 1.3em 1.5em 1.8em 2.0em 2.5em 3.0em',
                selector: selector,
                menubar: false,
                relative_urls: false,
                convert_urls: false,
                autoresize_min_height: 100,
                autoresize_max_height: 500,
                autoresize_bottom_margin: 0,
                statusbar: false,
                mentions: {
                    source: function (query, process, delimiter) {
                        // Do your ajax call
                        // When using multiple delimiters you can alter the query depending on the delimiter used
                        $.getJSON('/user/search', {username: query}, function (data) {
                            //call process to show the result
                            process(data)
                        });
                    },
                    highlighter: function(text) {
                        //make matched block italic
                        return text.replace(new RegExp('(@' + this.query + ')', 'ig'), function ($1, match) {
                            return '<em>' + match + '</em>';
                        });
                    },
                    insert: function(item) {
                        return '<a href="/profile/'+item.id+'">@' + item.name + '</a>';
                    }
                }
            });
        }
        loadEditor("textarea#comment");
    </script>


